import { Component } from '@angular/core';
import { categories } from '../categories';
import { products } from '../products';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css'],
})
export class ProductListComponent {
  products = [...products];
  categories = categories;
  currentCategory : number | undefined ;

  selectCategory(categoryId: number) {
    this.currentCategory = categoryId;
  } 

  handleCategory(category: number): any {
    this.currentCategory = category;
    return this.currentCategory === undefined ? products : products.filter((product: { categoryId: number }) => product.categoryId === category);
  }

  share(sh:string) {
    window.open(`https://t.me/share/url?url=${sh}&text=xssxcfscxscsc`);
  }

  onNotify() {
    window.alert('You will be notified when the product goes on sale');
  }
  
}

<h2>Products</h2>
<div id="category-buttons" *ngFor="let category of categories">
  <button type="bytton" (click)="selectCategory(category.id)">
      {{ category.title }}
  </button>
</div>

<div *ngIf="currentCategory != undefined">
<div *ngFor="let product of handleCategory(currentCategory)" class="product">
  <a href="{{product.href}}"><img src="{{product.img}}" width="{{product.width}}" height="{{product.height}}" ></a>
  <div class="info">
  <h3>
    <a [title]="product.name + ' details'" href="{{product.href}}">
      {{ product.name }}
    </a>
  </h3>
  
  <p *ngIf="product.description" class="desc">Description: {{ product.description }}</p>
  <div class="ratePrice">
  <p *ngIf="product.rating" class="rate">Rating: <span class="rating">{{product.rating}}/10</span> </p>
  <p > Price: <span class="price">{{product.price}} tg </span></p>
</div>
  <a href=""><button type="button" (click)="share(product.href)">Share</button></a>
  <app-product-alerts [product]="product" (notify)="onNotify()">
  </app-product-alerts>
  </div>
</div>
</div>
